// Lucide Icons - Lightweight icon library
// This would typically be the minified version from https://lucide.dev/
// For development, you can use the CDN version in your HTML:
// <script src="https://unpkg.com/lucide@latest/dist/umd/lucide.js"></script>

// Placeholder for local lucide implementation
window.lucide = {
  createIcons: function() {
    // Initialize Lucide icons
    if (typeof lucide !== 'undefined' && lucide.createIcons) {
      lucide.createIcons();
    }
  },
  
  // Common icon creation helper
  createElement: function(iconName, attributes = {}) {
    const icon = document.createElement('i');
    icon.setAttribute('data-lucide', iconName);
    
    Object.keys(attributes).forEach(key => {
      icon.setAttribute(key, attributes[key]);
    });
    
    return icon;
  }
};

// Auto-initialize when DOM is ready
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', () => {
    window.lucide.createIcons();
  });
} else {
  window.lucide.createIcons();
}